#include <iostream>
using namespace std;

void swap(int& a, int& b) {
    int temp = a;
    a = b;
    b = temp;
}

int main() {
    int x = 10, y = 20;
    swap(x, y);
    cout << "x: " << x << ", y: " << y << endl;
    return 0;
}

output:
public class Main {
    public static void swap(int[] a, int[] b) {
        int temp = a[0];
        a[0] = b[0];
        b[0] = temp;
    }

    public static void main(String[] args) {
        int x = 10, y = 20;
        swap(new int[] {} new int[] {y});
        System.out.println("x: " + x + ", y: " + y);
    }
}

