template <typename T>
void swapValues(T& a, T& b) {
    T temp = a;
    a = b;
    b = temp;
}

int main() {
    int x = 10, y = 20;
    swapValues(x, y);
    cout << "x: " << x << ", y: " << y << endl;
    return 0;
}

output:
public class Main {
    public static <T> void swapValues(T[] a, T[] b) {
        T temp = a[0];
        a[0] = b[0];
        b[0] = temp;
    }

    public static void main(String[] args) {
        int x = 10, y = 20;
        swapValues(new Integer[] {x}, new Integer[] {y});
        System.out.println("x: " + x + ", y: " + y);
    }
}

